mapper.select:
select * from commodity


         nftTokenId = #{nftTokenId}


        AND longitude = #{longitude}


        AND latitude = #{latitude}


        AND detailLoc = #{detailLoc}


        AND goodsId = #{goodsId}


        AND status = #{status}


        limit #{offset}, #{limit}

mapper.update:
update commodity set
latitude = #{latitude},
longitude = #{longitude},
detailLoc = #{detailLoc},
status = #{status}
where nftTokenId = #{nftTokenId}
